INSERT INTO `sys_role` (`role_id`, `role_code`, `role_name`, `description`, `create_user`, `create_date`, `update_user`, `update_date`, `state`) VALUES (1, 'R0001', 'Admin', 'Admin', 1, now(), 0, now(), 1);


insert into sys_role_resource(role_id, rsuc_id)
select 1, rsuc_id
from sys_resource
where state = 1;


INSERT INTO `sys_user_role` (`user_id`, `role_id`)
select user_id, 1
from dc_user
where state = 1;


alter table sys_mail_record modify column mail_title varchar(200)  comment 'Email title' not null default '';
alter table dc_system_conf modify column conf_value mediumtext  default null comment 'conf_value';
alter table dc_system_conf modify column `type` smallint not null default 1 comment 'Type   1: Portal information   2: Technical support 3: contact us   4: chain access information 5: Keystore  6: Treaty';

UPDATE sys_message_template SET msg_title = "${captcha_} is your verification code", msg_content = "Thank you for accessing the BSN Spartan Network!<br/><b>${captcha_}</b> is your verification code. This code is only valid for ${minutes_} minutes and will no longer be valid if you request a new code.", update_time = NOW() WHERE msg_code = "user_join_captcha_";
UPDATE sys_message_template SET msg_title = "${captcha_} is your verification code", msg_content = "Thank you for choosing the BSN Spartan Network!<br/><b>${captcha_}</b> is your verification code. This code is only valid for ${minutes_} minutes and will no longer be valid if you request a new code.", update_time = NOW() WHERE msg_code = "gas_recharge_captcha_";
UPDATE sys_message_template SET msg_title = "Order Submitted", msg_content = "We are pleased to inform you that we have received your order. Please complete the payment.<br/>Your order information is as follows:<br/>Order Number: ${trade_no_}<br/>Wallet: ${account_address_}<br/>Gas Credit Amount: ${gas_count_}<br/>Order Total: US$ ${pay_amount_}<br/>Payment Link: <a href='${pay_link_}'>${pay_link_}</a>", update_time = NOW() WHERE msg_code = "order_submit_success_";
UPDATE sys_message_template SET msg_title = "Order Submitted", msg_content = "We are pleased to inform you that we have received your order. Please complete the payment.<br>Your order information is as follows:<br>Order Number:${trade_no_}<br>Wallet:${account_address_}<br>Gas Credit Amount:${gas_count_}<br>Order Total:US$ ${pay_amount_}<br>Payment Information:<br>Bank Name: ${bank_name_}<br>Bank Account No.: ${bank_account_no_}<br>Bank Address: ${bank_address_}<br>Swift Code: ${swift_code_}<br>Payment Details: ${payment_details_}<br>Note: Please enter the order number in Payment Details so we can top up your Gas Credits", update_time = NOW() WHERE msg_code = "order_submit_offline_";
UPDATE sys_message_template SET msg_title = "Payment Success", msg_content = "We are pleased to inform you that we have received your order payment and we will recharge your Gas Credit and send the Gas Credit recharge result to your email address.<br/>Your order information is as follows:<br/>Order Number: ${trade_no_}<br/>Wallet: ${account_address_}<br/>Gas Credit Amount: ${gas_count_}<br/>Order Total: US$ ${pay_amount_}<br/>Payment Date: ${pay_time_}<br/> Transaction Hash: ${tx_hash_}", update_time = NOW() WHERE msg_code = "payment_success_";
UPDATE sys_message_template SET msg_title = "Gas Credit Top-up Processed", msg_content = "We are pleased to inform you that your recent Gas Credit top-up has been processed and you can check the balance here:${address_link_}<br/>Your transaction information is as follows:<br/>Order Number:${trade_no_}<br/>Wallet:${account_address_}<br/>Amount:${gas_count_}<br/>Order Total:US$ ${pay_amount_}<br/>Top-Up Time:${tx_time_}<br/>Transaction Hash:${tx_hash_}<br/><br/>Please check your Gas Credit balance.  You can get additional information via your Data Center Management System.", update_time = NOW() WHERE msg_code = "gas_recharge_success_";
UPDATE sys_message_template SET msg_title = "Configure Keystore Password", msg_content = "The system did not detect your keystore password and cannot submit transactions to the chain. Please log in to the Data Center Management System and configure the keystore password in the Keystore Password section.", update_time = NOW() WHERE msg_code = "keyStorePasswordConfig";
UPDATE sys_message_template SET msg_title = "Network Access Information", msg_content = "Welcome to the BSN Spartan Network!<br/>You can access the BSN Spartan Network via these nodes:<br/><br/>Spartan-I(Powered by NC Ethereum)<br/>Access Key:${accessKey_}<br/>Access Url Example:${accessDemo_}<br/>Json RPC Access URL:${ethRpc_}<br/>WebSocket Access URL:${ethWs_}<br/><br/>Spartan-II(Powered by NC Cosmos)<br/>Access Key:${accessKey_}<br/>Json RPC:<br/>EVM module:${cosmosEvmRpc_}<br/>Native module:${cosmosRpc_}<br/>WebSocket:<br/>EVM module:${cosmosEvmWs_}<br/>Native module:${cosmosWs_}<br/>gRPC:${cosmosGrpc_}<br/><br/>Spartan-III(Powered by NC PolygonEdge)Access Key:${accessKey_}<br/>Json RPC Access URL:${poloygonRpc_}<br/>WebSocket Access URL:${polygonWs_}<br/><br/>", update_time = NOW() WHERE msg_code = "user_join_email_";
UPDATE sys_message_template SET msg_title = "Re-configure Keystore Password", msg_content = "After restarting the service of the Data Center Management System, please configure the keystore password again or else the system cannot submit transactions to the chain.<br/><b>Operation step</b>: Click 'Configuration' button and enter the keystore password in the Keystore Password section.", update_time = NOW() WHERE msg_code = "keystorePasswordReset";


INSERT INTO `sys_message_template` (`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`,
                                    `state`)
VALUES ('gasBalanceReminderConfig', 'Insufficient Gas Credit',
        'Transactions cannot be submitted to the chain because there isn’t sufficient Gas Credit in your NTT Wallet Address. Please log in to the Data Center Management System and recharge the Gas Credit by clicking the Emergency button on the home page.',
        '2', now(), now(), '1');

