CREATE
DATABASE bsn_spartan_dc DEFAULT CHARACTER SET utf8 COLLATE = utf8_general_ci;

use
bsn_spartan_dc;


CREATE TABLE dc_user
(
    user_id        BIGINT      NOT NULL auto_increment,
    contacts_email VARCHAR(60) NOT NULL DEFAULT '' COMMENT 'Email Address',
    contacts_name  VARCHAR(50) NOT NULL DEFAULT '' COMMENT 'Contact',
    contacts_phone VARCHAR(50) NOT NULL DEFAULT '' COMMENT 'Phone',
    state          SMALLINT    NOT NULL DEFAULT 1 COMMENT 'Status   1: Enabled   0: Disabled',
    create_time    datetime COMMENT 'Created time',
    salt           VARCHAR(32) NOT NULL DEFAULT '' COMMENT 'Salt',
    PASSWORD       VARCHAR(50) NOT NULL DEFAULT '' COMMENT 'Password',
    PRIMARY KEY (user_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'User Information List';


CREATE TABLE ntt_tx_record
(
    tx_record_id     BIGINT          NOT NULL auto_increment,
    tx_hash          VARCHAR(100)    NOT NULL DEFAULT '' COMMENT 'Transaction Hash',
    operator         VARCHAR(100)    NOT NULL DEFAULT '' COMMENT 'Sender address',
    type             SMALLINT        NOT NULL DEFAULT 1 COMMENT 'Type   0: Node Establishment Incentive Program  1  : Data Center Monthly Incentive Program    3: Top up Gas Credit   4: Top up NTT   7: Refund for Gas Credit top-up failed',
    from_address     VARCHAR(100)    NOT NULL DEFAULT '' COMMENT 'Sender wallet address ',
    to_address       VARCHAR(100)    NOT NULL DEFAULT '' COMMENT ' Receiver wallet address',
    ntt_count        DECIMAL(56, 12) NOT NULL DEFAULT 0 COMMENT 'Nubmer of NTTs',
    from_ntt_balance DECIMAL(56, 12) NOT NULL DEFAULT 0 COMMENT 'Sender wallet balance ',
    to_ntt_balance   DECIMAL(56, 12) NOT NULL DEFAULT 0 COMMENT ' Receiver wallet balance',
    tx_time          datetime COMMENT 'Transaction time',
    create_time      datetime COMMENT 'Created time',
    md5_sign         VARCHAR(64)              DEFAULT '' COMMENT 'The hash value generated by MD5 algorithm of the data obtained from the parsed block',
    PRIMARY KEY (tx_record_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'NTT transaction table';


CREATE TABLE event_block
(
    block_height BIGINT NOT NULL DEFAULT 0 COMMENT 'Block height',
    PRIMARY KEY (block_height)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'Event parsing block height';


CREATE TABLE chain_price
(
    chain_price_id BIGINT          NOT NULL auto_increment,
    chain_id       BIGINT,
    ntt_count      NUMERIC(56, 12) NOT NULL DEFAULT 1 COMMENT 'NTT',
    gas            NUMERIC(56, 12) NOT NULL DEFAULT 0 COMMENT 'Gas Credit price',
    create_time    datetime COMMENT 'Created time',
    PRIMARY KEY (chain_price_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'Price information';


CREATE TABLE sys_data_center
(
    dc_id               BIGINT       NOT NULL auto_increment COMMENT 'Data Center ID',
    contacts_email      VARCHAR(60)  NOT NULL DEFAULT '' COMMENT 'Email Address',
    ntt_account_address VARCHAR(100) NOT NULL DEFAULT '' COMMENT 'NTT Wallet Address',
    token               VARCHAR(500) NOT NULL DEFAULT '' COMMENT 'Token',
    dc_code             VARCHAR(20)  NOT NULL DEFAULT '' COMMENT 'ID',
    logo                mediumtext COMMENT 'logo',
    create_time         datetime COMMENT 'Created time',
    PRIMARY KEY (dc_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'Data Center Information Table';


CREATE TABLE dc_chain
(
    chain_id         BIGINT       NOT NULL auto_increment,
    chain_type       VARCHAR(50)  NOT NULL DEFAULT '' COMMENT 'Chain Type',
    chain_name       VARCHAR(100) NOT NULL DEFAULT '' COMMENT 'Chain Name',
    recharge_unit    VARCHAR(10)  NOT NULL DEFAULT '' COMMENT 'unit',
    chain_code       varchar(20)  NOT NULL DEFAULT '' COMMENT 'Chain request ID   unique:  After the business system is successfully configured, the gateway needs to be modified',
    gateway_type     smallint(6) NOT NULL DEFAULT '0' COMMENT 'whether support gateway    0: Not supported    1: supported',
    gateway_url      varchar(200) NOT NULL DEFAULT '' COMMENT 'Gateway URL',
    ws_gateway_url   varchar(200) NOT NULL DEFAULT '' COMMENT 'websocketgateway URL',
    grpc_gateway_url varchar(200) NOT NULL DEFAULT '' COMMENT 'Json RPC  0: Not supported 1: Supported',
    json_rpc         smallint(6) NOT NULL DEFAULT '0' COMMENT 'Json RPC  0: Not supported 1: Supported',
    websocket        smallint(6) NOT NULL DEFAULT '0' COMMENT 'WebSocket 0: Not supported 1: Supported',
    grpc             smallint(6) NOT NULL DEFAULT '0' COMMENT 'gRPC 0: Not supported 1: Supported',
    PRIMARY KEY (chain_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'Data Center - Chain Information Table';


CREATE TABLE chain_account_recharge_meta
(
    recharge_meta_id      BIGINT         NOT NULL auto_increment,
    chain_account_address VARCHAR(100) COMMENT 'Wallet Address',
    gas                   NUMERIC(50, 0) NOT NULL DEFAULT 0 COMMENT 'Topped-up Gas Credit',
    deadline              BIGINT         NOT NULL DEFAULT 0 COMMENT 'Transaction Expiration Time',
    sign                  VARCHAR(500)   NOT NULL DEFAULT '' COMMENT 'Signature    Byte value compiled by base64',
    PRIMARY KEY (recharge_meta_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'Emergency Top-up Gas Credit Information Table';


CREATE TABLE dc_gas_recharge_record
(
    recharge_record_id BIGINT       NOT NULL auto_increment,
    chain_id           bigint(20) DEFAULT NULL,
    order_id           bigint(20) DEFAULT NULL,
    chain_address      VARCHAR(100) NOT NULL COMMENT 'Wallet Address',
    gas                NUMERIC(50)  NOT NULL DEFAULT 0,
    recharge_time      datetime              DEFAULT CURRENT_TIMESTAMP COMMENT 'Top-up Time',
    state              SMALLINT     NOT NULL DEFAULT 0 COMMENT 'Top-up submission status   1: Pending    2: In Progress    3: Submitted    4: Failed',
    tx_hash            VARCHAR(200)          DEFAULT NULL COMMENT 'Transaction hash',
    update_time        datetime              DEFAULT NULL COMMENT 'Update time',
    recharge_code      VARCHAR(64)  NOT NULL DEFAULT '' COMMENT 'Top-up ID: the event is returned from the chain',
    ntt                NUMERIC(56, 12) COMMENT 'NTT consumed by topping up Gas Credit  Event parsing data',
    recharge_result    VARCHAR(500) COMMENT 'Result:  if success, returns the transaction hash; if failed, returns the reason',
    recharge_state     SMALLINT     NOT NULL DEFAULT 3 COMMENT 'Top-up status   0: In Progress   1: Success    2: Failed  3: No processing required',
    nonce              BIGINT       NOT NULL DEFAULT 0 COMMENT "nonce of emergency Gas Credit top-up, corresponding to the table's primary key",
    md5_sign           VARCHAR(64)           DEFAULT '' COMMENT 'The hash value generated by MD5 algorithm of the data obtained from the parsed block',
    PRIMARY KEY (recharge_record_id)
) ENGINE = INNODB DEFAULT CHARSET = utf8 COMMENT 'Gas Credit top-up record table  (End-user Gas Credit top-up + emergency Gas Credit top-up (event parsing))';


CREATE TABLE `dc_node`
(
    `node_id`              BIGINT (20) NOT NULL AUTO_INCREMENT,
    `chain_id`             BIGINT (20) DEFAULT NULL,
    `node_code`            VARCHAR(200) NOT NULL DEFAULT '' COMMENT 'Node ID',
    `rpc_address`          VARCHAR(100) NOT NULL DEFAULT '' COMMENT 'RPC address',
    `node_address`         VARCHAR(500) NOT NULL DEFAULT '' COMMENT 'Node address',
    `apply_sign`           VARCHAR(500) NOT NULL DEFAULT '' COMMENT 'Node registration data signature',
    `state`                SMALLINT (6) NOT NULL DEFAULT '0' COMMENT 'Status  0: Pending   1: Submitted 2: Node registration testing   3: Failed    4: Success',
    `remarks`              VARCHAR(500)          DEFAULT NULL COMMENT 'Remarks',
    `create_time`          datetime     NOT NULL COMMENT 'Date applied',
    `tx_hash`              VARCHAR(100)          DEFAULT NULL COMMENT 'Transaction Hash',
    `tx_time`              datetime              DEFAULT NULL COMMENT 'Transaction time',
    `apply_result_time`    datetime              DEFAULT NULL COMMENT 'Date of notifying the node registration application result',
    `apply_result_tx_hash` VARCHAR(100)          DEFAULT NULL COMMENT 'Transaction hash of the node registration application',
    `ntt_count`            DECIMAL(56, 12)       DEFAULT '0.000000000000' COMMENT 'Amount of NTT Incentives',
    `reason`               VARCHAR(500)          DEFAULT ' ' COMMENT 'Reason',
    PRIMARY KEY (`node_id`)
) ENGINE = INNODB AUTO_INCREMENT = 1 DEFAULT CHARSET = utf8 COMMENT = 'Full Node Information Table';


CREATE TABLE ntt_tx_sum
(
    `ntt_tx_sum_id` BIGINT (20) NOT NULL AUTO_INCREMENT COMMENT 'ntt_tx_sum_id',
    `flow_in`       DECIMAL(56, 12) NOT NULL DEFAULT '0.000000000000' COMMENT 'in',
    `flow_out`      DECIMAL(56, 12) NOT NULL DEFAULT '0.000000000000' COMMENT 'out',
    PRIMARY KEY (`ntt_tx_sum_id`)
) ENGINE = INNODB AUTO_INCREMENT = 1 DEFAULT CHARSET = utf8 COMMENT = 'NTT transaction summary';



INSERT INTO `dc_chain` (`chain_id`, `chain_type`, `chain_name`, `recharge_unit`)
VALUES ('1', 'ETH', 'Spartan-I Chain (Powered by NC Ethereum)', 'GWEI'),
       ('2', 'Cosmos', 'Spartan-II Chain (Powered by NC Cosmos)', 'UGAS'),
       ('3', 'Polygon', 'Spartan-III Chain (Powered by NC PolygonEdge)', 'GWEI');



create table dc_payment_order
(
    `order_id`           bigint(20) NOT NULL AUTO_INCREMENT,
    `chain_id`           bigint(20) DEFAULT NULL,
    `sale_price_id`      bigint(20) DEFAULT NULL,
    `payment_type_id`    bigint(20) NOT NULL DEFAULT '0' COMMENT 'Payment method',
    `trade_no`           varchar(50)     NOT NULL DEFAULT '' COMMENT 'Transaction No. (created by the sytem)',
    `other_trade_no`     varchar(50)     NOT NULL DEFAULT '' COMMENT 'Third-party transaction No.',
    `payment_intent`     varchar(50)              DEFAULT '' COMMENT 'payment information',
    `account_address`    varchar(100)    NOT NULL DEFAULT '' COMMENT 'wallet address',
    `email`              varchar(50)     NOT NULL DEFAULT '' COMMENT 'email address',
    `currency`           varchar(20)     NOT NULL DEFAULT '' COMMENT 'Currency type when pay by fiat currency: USD......； when pay by stablecoins: USDC......',
    `ex_rates`           varchar(50)     NOT NULL DEFAULT '' COMMENT 'Gas Credit ratio',
    `gas_count`          decimal(56, 0)  NOT NULL DEFAULT '0' COMMENT 'Amount of Gas Credit',
    `pay_account`        varchar(100)    NOT NULL DEFAULT '' COMMENT 'Address for payment',
    `pay_amount`         decimal(56, 12) NOT NULL DEFAULT '0.000000000000' COMMENT 'Payment amount',
    `pay_state`          smallint(6) NOT NULL DEFAULT '0' COMMENT 'Payment status     0: payment in progress   1: payment successful 2: payment failed',
    `pay_time`           datetime                 DEFAULT NULL COMMENT 'Payment time',
    `tx_hash`            varchar(100)    NOT NULL DEFAULT '' COMMENT 'Transaction hash',
    `tx_time`            datetime                 DEFAULT NULL COMMENT 'Transaction time',
    `gas_tx_hash`        varchar(100)    NOT NULL DEFAULT '' COMMENT 'Gas Credit top-up transaction hash',
	`gas_tx_time`        datetime        DEFAULT NULL COMMENT 'Gas Credit top-up time',
    `gas_recharge_state` smallint(6) NOT NULL DEFAULT '3' COMMENT 'Top-up status     0: Top-up in progress   1: Top-up successful 2: Top-up failed   3: Pending',
    `is_refund`          smallint(6) NOT NULL DEFAULT '0' COMMENT 'Refund 0: Not refunded 1: Refunded',
    `refund_amount`      decimal(56, 12) NOT NULL DEFAULT '0.000000000000' COMMENT 'Refund amount',
    `remarks`            varchar(200)    NOT NULL DEFAULT '' COMMENT 'Remarks',
    `update_time`        datetime        NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Update time',
    `create_time`        datetime        NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Created time',
    primary key (order_id)
) ENGINE = INNODB
  DEFAULT CHARSET = utf8 comment  'Gas payment order table';


create table dc_payment_refund
(
    refund_id       bigint          not null auto_increment,
    order_id        bigint,
    trade_no        varchar(50)     not null default '' comment 'Transaction No. (created by the sytem)',
    other_trade_no  varchar(50)     not null default '' comment 'Third-party transaction No.',
    account_address varchar(100)    not null default '' comment 'wallet address',
    refund_amount   decimal(56, 12) not null default 0 comment 'Refund amount',
    refund_state    smallint        not null default 0 comment 'Refund status     0: Refund in progress   1: Refund successful 2: Refund failed',
    refund_time     datetime comment 'Refund time',
    remarks         varchar(200)    not null default '' comment 'Remarks',
    refund_receipt  varchar(200)             DEFAULT '' COMMENT 'Refund receipt',
    operator        bigint          not null default 0 comment 'Operator',
    update_time     datetime        not null default CURRENT_TIMESTAMP comment 'Update time',
    create_time     datetime        not null default CURRENT_TIMESTAMP comment 'Created time',
    primary key (refund_id)
) ENGINE = INNODB
  DEFAULT CHARSET = utf8 comment  'GAS refund table';


create table dc_payment_type
(
    payment_type_id  bigint       not null auto_increment,
    pay_type         smallint     not null default 0 comment 'Payment type 1: Fiat currency 2: stablecoins   3: remittance',
    pay_channel_name varchar(50)  not null default '' comment 'Payment channel    stripe 、coinbase',
    channel_code     varchar(10)  not null default '' comment 'channel ID',
    private_key      varchar(300) not null default '' comment 'Private key',
    endpoint_secret  varchar(100) not null default '' comment 'Callback signature key',
    api_key          varchar(50)  not null default '' comment 'api_key',
    api_version      varchar(20)  not null default '' comment 'api_version',
    bank_name        varchar(50)  not null default '' comment 'Bank name',
    bank_account     varchar(50)  not null default '' comment 'Bank account',
    bank_address     varchar(50)  not null default '' comment 'Bank address',
    swift_code       varchar(50)  not null default '' comment 'swift code',
    enable_status    smallint(1) not null default 1 comment 'Enablement status 0:enabled 1:disabled',
    create_time      datetime     not null default CURRENT_TIMESTAMP comment 'Created time',
    update_time      datetime     not null default CURRENT_TIMESTAMP comment 'Update time',
    primary key (payment_type_id)
) ENGINE = INNODB
  DEFAULT CHARSET = utf8 comment  'Payment method table';


create table dc_system_conf
(
    conf_id     bigint        not null auto_increment,
    type        smallint      not null default 1 comment 'Type   1: Portal information   2: Technical support 3: contact us   4: chain access information 5: Keystore  6: Treaty',
    conf_code   varchar(50)   not null default '' comment 'conf_code',
    conf_value  mediumtext default null comment 'conf_value',
    update_time datetime comment 'Update time',
    primary key (conf_id)
) ENGINE = INNODB
  DEFAULT CHARSET = utf8 comment  'Configuration information table';


create table chain_sale_price
(
    sale_price_id  bigint       not null auto_increment,
    chain_price_id bigint,
    chain_id       bigint,
    gas            decimal(56)  not null default 1 comment 'Gas Credit amount',
    sale_price     decimal(56)  not null default 0 comment 'Gas Credit Price (USD cent)',
    state          smallint     not null default 0 comment 'Status 1: pending review 2: approved 3: not approved',
    start_time     datetime comment 'Effective date',
    end_time       datetime comment 'Expiry date',
    create_user_id bigint       not null default 0 comment 'Created by',
    create_time    datetime     not null default CURRENT_TIMESTAMP comment 'Created time',
    check_user_id  bigint       not null default 0 comment 'Reviewed by',
    check_time     datetime     not null default CURRENT_TIMESTAMP comment 'Review time',
    check_remark   varchar(200) not null default "" comment 'Comment',
    primary key (sale_price_id)
) ENGINE = INNODB
  DEFAULT CHARSET = utf8 comment  'Price information table';


create table dc_chain_access
(
    chain_access_id    bigint      not null auto_increment,
    access_key         varchar(32) not null comment 'Access key   UUID',
    contacts_email     varchar(60) not null default '' comment 'email address',
    state              smallint             default 1 comment 'Status    1:enabled(default)，2 disabled，3 deleted',
    tps                int         not null default -1 comment 'TPS  -1 means no limits',
    tpd                int         not null default -1 comment 'TPD  -1 means no limits',
    stripe_customer_id varchar(50) not null default '' comment 'Stripe user id',
    create_time        datetime    not null default CURRENT_TIMESTAMP comment 'Created time',
    update_time        datetime    not null default CURRENT_TIMESTAMP comment 'Update time',
    notify_state       smallint             default 1 comment 'Notify Gateway Status 1: success 2: failed',
    primary key (chain_access_id)
) ENGINE = INNODB
  DEFAULT CHARSET = utf8 comment  'User access record table';


CREATE TABLE `sys_message_template`
(
    `template_id` bigint(20) NOT NULL AUTO_INCREMENT,
    `msg_code`    varchar(50)   NOT NULL DEFAULT '' COMMENT 'Template ID',
    `msg_title`   varchar(100)           DEFAULT NULL COMMENT 'Template title',
    `msg_content` varchar(2000) NOT NULL DEFAULT '' COMMENT 'Template content',
    `msg_type`    smallint(6) NOT NULL DEFAULT '0' COMMENT 'Message type    1: Station letter 2: email  ',
    `update_time` datetime      NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Update time',
    `create_time` datetime      NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Created time',
    `state`       smallint(6) NOT NULL DEFAULT '1' COMMENT 'Status，1: enabled       0: disabled',
    PRIMARY KEY (`template_id`)
) ENGINE=InnoDB
  DEFAULT CHARSET=utf8 COMMENT='Email template table';


CREATE TABLE `sys_mail_record`
(
    `record_id`    bigint(20) NOT NULL AUTO_INCREMENT,
    `template_id`  bigint(20) DEFAULT NULL,
    `sender`       varchar(50)   NOT NULL DEFAULT '' COMMENT 'Sender',
    `receiver`     varchar(200)  NOT NULL DEFAULT '' COMMENT 'Recipient',
    `cc_people`    varchar(200)           DEFAULT NULL COMMENT 'CC',
    `mail_title`   varchar(200)  NOT NULL DEFAULT '' COMMENT 'Email title',
    `mail_content` varchar(1000) NOT NULL DEFAULT '' COMMENT 'Email content',
    `state`        smallint(6) NOT NULL DEFAULT '1' COMMENT 'Sending Status，1: successful 0: failed',
    `send_time`    datetime      NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'Send time',
    PRIMARY KEY (`record_id`)
) ENGINE=InnoDB
  DEFAULT CHARSET=utf8 COMMENT='Send email record table';
  
CREATE TABLE `sys_role` (
                            `role_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'Role id',
                            `role_code` varchar(20) NOT NULL DEFAULT '' COMMENT 'Role code',
                            `role_name` varchar(20) NOT NULL DEFAULT '' COMMENT 'Role name',
                            `description` varchar(500) DEFAULT '' COMMENT 'Description',
                            `create_user` bigint(20) NOT NULL,
                            `create_date` datetime NOT NULL COMMENT 'Created time',
                            `update_user` bigint(20) DEFAULT NULL COMMENT 'Modified by',
                            `update_date` datetime DEFAULT NULL COMMENT 'Update time',
                            `state` smallint(6) NOT NULL DEFAULT '0' COMMENT 'Status   1: Enabled   2: Disabled',
                            PRIMARY KEY (`role_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='Role table';


CREATE TABLE `sys_user_role` (
                                 `user_role_id` bigint(20) NOT NULL AUTO_INCREMENT,
                                 `user_id` bigint(20) DEFAULT NULL,
                                 `role_id` bigint(20) DEFAULT NULL,
                                 PRIMARY KEY (`user_role_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='User role table';

CREATE TABLE `sys_resource` (
                                `rsuc_id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT 'Resource id',
                                `parent_id` bigint(20) NOT NULL DEFAULT '0' COMMENT 'Parent number',
                                `rsuc_code` varchar(50) NOT NULL DEFAULT '' COMMENT 'Resource code',
                                `rsuc_name` varchar(50) NOT NULL DEFAULT '' COMMENT 'Resource name',
                                `rsuc_url` varchar(150) NOT NULL DEFAULT '' COMMENT 'Resource path',
                                `priority` int(11) NOT NULL DEFAULT '0' COMMENT 'Sequence',
                                `rsuc_type` smallint(6) NOT NULL DEFAULT '1' COMMENT 'Permission Category 1: Page 2: Button',
                                `state` smallint(6) NOT NULL DEFAULT '1' COMMENT 'Status   1: Enabled   2: Disabled',
                                PRIMARY KEY (`rsuc_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='Resource table';


CREATE TABLE `sys_role_resource` (
                                     `role_resource_id` bigint(20) NOT NULL AUTO_INCREMENT,
                                     `role_id` bigint(20) DEFAULT NULL,
                                     `rsuc_id` bigint(20) DEFAULT NULL,
                                     PRIMARY KEY (`role_resource_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='Role resource table';


