INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('email_template', 'Email template', '<blockquote style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0.5em;\"><div><div><table align=\"center\" cellspacing=\"0\" cellpadding=\"0\" style=\"width: 700px;border: 1px solid #9B9B9B;\"><tbody><tr><td style=\"background-color:#013893;height:60px;\"><img src=\"${logo}\" style=\"margin:10px; height:36px;\"></td></tr><tr><td><table width=\"700\" style=\"padding-left: 24px;padding-right: 24px;\"><tbody><tr><td style=\"padding-top: 18px; font-size: 14px; color: #000000;\"><span>Hello there,</span></td></tr><tr><td style=\"padding-top: 24px; font-size: 14px; color: #000000;word-break:break-word;overflow: hidden;\"><span style=\"line-height:200%;\">${text}</span></td></tr>${contact}</tbody></table></td></tr></tbody></table><p style=\"text-align: center;font-size: 12px;line-height: 20px;color: #4A4A4A;\">This email is automatically sent by the system, please do not reply, and if you are not using BSN Spartan Network, please ignore.</p></div></div></blockquote>', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('contact_template_', 'Contact information email template', '<tr><td style=\"font-size: 14px; color: #000000;padding-top: 15px;padding-bottom: 15px;\"><span>Thank you for using BSN Spartan Network!</span></td></tr><tr><td style=\"font-size: 14px; color: #000000;\"><span>If you have any questions, please contact us by the following ways:</span></td></tr><tr><td style=\"font-size: 14px; padding-top: 5px;\">-Telegram:&nbsp;<a target=\"_blank\" href=\"https://t.me/bsnsupport\">https://t.me/bsnsupport</a></td></tr><tr><td style=\"font-size: 14px; padding-top: 5px;\">-Whatsapp:&nbsp;<a target=\"_blank\" href=\"https://chat.whatsapp.com/H9wENt0lw4dFTVopaILFxh\">https://chat.whatsapp.com/H9wENt0lw4dFTVopaILFxh</td></tr><tr><td style=\"font-size: 14px; padding-top: 5px;\">-Reddit:&nbsp;<a target=\"_blank\" href=\"https://www.reddit.com/r/BSNSpartanSupport/\">https://www.reddit.com/r/BSNSpartanSupport/</a></td></tr><tr><td style=\"font-size: 14px; padding-top: 5px;\">-Discord:&nbsp;<a target=\"_blank\" href=\"https://discord.gg/HEpnKTUyAX\">https://discord.gg/HEpnKTUyAX</a></td></tr><tr><td style=\"font-size: 14px; padding-top: 5px;\">-Email:&nbsp;<a href=\"mailto:BSN_Spartan_support@reddatetech.com.hk\">BSN_Spartan_support@reddatetech.com.hk</td></tr><tr><td>&nbsp;</td></tr>', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('user_join_captcha_', 'BSN Spartan Data Center Notification: <b>${captcha_}</b> is your verification code', 'Thank you for accessing the BSN Spartan Network!<br/><b>${captcha_}</b> is your verification code. This code is only valid for ${minutes_} minutes and will no longer be valid if you request a new code.', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('gas_recharge_captcha_', 'BSN Spartan Data Center Notification: <b>${captcha_}</b> is your verification code', 'Thank you for choosing the BSN Spartan Network!<br/><b>${captcha_}</b> is your verification code. This code is only valid for ${minutes_} minutes and will no longer be valid if you request a new code.', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('user_join_email_', 'BSN Spartan Data Center Notification: Network Access Information', 'Welcome to the BSN Spartan Network!<br/>You can access the BSN Spartan Network via these nodes:<br/><br/>Spartan-I(Powered by NC Ethereum)<br/>Access Key:${accessKey_}<br/>Access Url Example:${accessDemo_}<br/>Json RPC Access URL:${ethRpc_}<br/>WebSocket Access URL:${ethWs_}<br/><br/>Spartan-II(Powered by NC Cosmos)<br/>Access Key:${accessKey_}<br/>Json RPC:<br/>EVM module:${cosmosEvmRpc_}<br/>Native module:${cosmosRpc_}<br/>WebSocket:<br/>EVM module:${cosmosEvmWs_}<br/>Native module:${cosmosWs_}<br/>gRPC:${cosmosGrpc_}<br/><br/>Spartan-III(Powered by NC PolygonEdge)Access Key:${accessKey_}<br/>Json RPC Access URL:${poloygonRpc_}<br/>WebSocket Access URL:${polygonWs_}<br/><br/>', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('order_submit_offline_', 'BSN Spartan Data Center Notification: Order Submitted', 'We are pleased to inform you that we have received your order. Please complete the payment.<br>Your order information is as follows:<br>Order Number:${trade_no_}<br>Wallet:${account_address_}<br>Gas Credit Amount:${gas_count_}<br>Order Total:US$ ${pay_amount_}<br>Payment Information:<br>Bank Name: ${bank_name_}<br>Bank Account No.: ${bank_account_no_}<br>Bank Address: ${bank_address_}<br>Swift Code: ${swift_code_}<br>Payment Details: ${payment_details_}<br>Note: Please enter the order number in Payment Details so we can top up your Gas Credits', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('order_submit_success_', 'BSN Spartan Data Center Notification: Order Submitted', 'We are pleased to inform you that we have received your order. Please complete the payment.<br/>Your order information is as follows:<br/>Order Number: ${trade_no_}<br/>Wallet: ${account_address_}<br/>Gas Credit Amount: ${gas_count_}<br/>Order Total: US$ ${pay_amount_}<br/>Payment Link: <a href=\'${pay_link_}\'>${pay_link_}</a>', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('payment_success_', 'BSN Spartan Data Center Notification: Payment Success', 'We are pleased to inform you that we have received your order payment and we will recharge your Gas Credit and send the Gas Credit recharge result to your email address.<br/>Your order information is as follows:<br/>Order Number: ${trade_no_}<br/>Wallet: ${account_address_}<br/>Gas Credit Amount: ${gas_count_}<br/>Order Total: US$ ${pay_amount_}<br/>Payment Date: ${pay_time_}<br/> Transaction Hash: ${tx_hash_}', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('keystorePasswordReset', 'BSN Spartan Data Center Notification: Re-configure Keystore Password', 'After restarting the service of the Data Center Management System, please configure the keystore password again or else the system cannot submit transactions to the chain.<br/><b>Operation step</b>: Click \'Configuration\' button and enter the keystore password in the Keystore Password section.', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('gas_recharge_success_', 'BSN Spartan Data Center Notification: Emergency Gas Credit Top-up Processed', 'We are pleased to inform you that your recent Gas Credit top-up has been processed and you can check the balance here:${address_link_}<br/>Your transaction information is as follows:<br/>Order Number:${trade_no_}<br/>Wallet:${account_address_}<br/>Amount:${gas_count_}<br/>Order Total:US$${pay_amount_}<br/>Top-Up Time:${tx_time_}<br/>Transaction Hash:${tx_hash_}<br/><br/>Please check your Gas Credit balance.  You can get additional information via your Data Center Management System.', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (now()`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`, `state`) VALUES ('keyStorePasswordConfig', 'BSN Spartan Data Center Notification: Configure Keystore Password', 'The system did not detect your keystore password and cannot submit transactions to the chain. Please log in to the Data Center Management System and configure the keystore password in the Keystore Password section.', 2, now(), now(), 1);
INSERT INTO `sys_message_template` (`msg_code`, `msg_title`, `msg_content`, `msg_type`, `update_time`, `create_time`,
                                    `state`)
VALUES ('gasBalanceReminderConfig', 'Insufficient Gas Credit',
        'Transactions cannot be submitted to the chain because there isn’t sufficient Gas Credit in your NTT Wallet Address. Please log in to the Data Center Management System and recharge the Gas Credit by clicking the Emergency button on the home page.',
        '2', now(), now(), '1');
