alter table dc_gas_recharge_record add column audit_state smallint not null default 0 comment "Review Status 0:Pending Review 1:Approved 2:Not Approved";
alter table dc_gas_recharge_record add column auditor bigint comment "Reviewed by";
alter table dc_gas_recharge_record add column audit_time datetime comment "Review Time";
alter table dc_gas_recharge_record add column audit_remarks varchar(300) default "" comment "Comment";
alter table dc_gas_recharge_record add column create_user_id bigint comment "Creator";
alter table dc_gas_recharge_record add column create_time datetime not null default CURRENT_TIMESTAMP comment "Created Time";
ALTER TABLE dc_gas_recharge_record MODIFY COLUMN recharge_time datetime COMMENT 'Recharge Time' AFTER `gas`;
alter table dc_gas_recharge_record add column recharge_type smallint not null default 0 comment "Recharge Type 0：Free 1：Paid";

create table dc_terms_service_audit
(
    service_audit_id     bigint not null auto_increment,
    terms_content        mediumtext not null comment 'Terms and Conditions',
    creator              bigint not null default 0 comment 'Creator',
    create_time          datetime not null default CURRENT_TIMESTAMP comment 'Created Time',
    auditor              bigint not null default 0 comment 'Reviewed by',
    audit_time           datetime comment 'Review Time',
    audit_state          smallint not null default 0 comment 'Review Status 0:Pending Review 1:Approved 2:Not Approved',
    remark               varchar(300) not null default '',
    primary key (service_audit_id)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT='Terms of Service Review Form';


create table dc_account_balance_conf
(
    account_balance_conf_id bigint not null auto_increment,
    chain_id             bigint default NULL comment 'Chain ID',
    chain_address        varchar(100) not null comment 'chain Account Address',
    monitor_type         smallint not null default 1 comment 'Account Monitor Type 1：NTT 2:GAS',
    balance_limit        decimal(30,0)  default null comment 'NTT Balance Alert Value',
    reminder_email       varchar(1000) not null default '' comment 'Emial',
    state                smallint default 1 comment 'Status 0:disabled 1:enabled',
    create_time          datetime not null default CURRENT_TIMESTAMP comment 'Created time',
    primary key (account_balance_conf_id)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 comment 'Account Balance Monitor Configuration';

CREATE TABLE `dc_mail_conf` (
    `mail_conf_id` bigint(20) NOT NULL AUTO_INCREMENT,
    `mail_host` varchar(50) NOT NULL DEFAULT '' COMMENT 'mail_host',
    `mail_port` varchar(10) NOT NULL DEFAULT '' COMMENT 'mail_port',
    `mail_user_name` varchar(50) NOT NULL DEFAULT '' COMMENT 'Email',
    `mail_pass_word` varchar(150) NOT NULL DEFAULT '' COMMENT 'Password',
    `properties` varchar(1000) NOT NULL DEFAULT '' COMMENT 'properties',
    `type` smallint(6) NOT NULL DEFAULT '1' COMMENT 'Type   1：java mail   2：aws',
    `access_key` varchar(100) NOT NULL DEFAULT '' COMMENT 'access_key',
    `secret_key` varchar(100) NOT NULL DEFAULT '' COMMENT 'secret_key',
    `region_static` varchar(50) NOT NULL DEFAULT '' COMMENT 'area',
    `state` smallint(6) NOT NULL DEFAULT '1' COMMENT 'Status 0:enabled 1:disabled',
    `create_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Created time',
    `update_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT 'Update time',
    PRIMARY KEY (`mail_conf_id`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8 COMMENT 'Email Setting';


